function startFootPrints(n) {

    var a = startingAngle * Math.PI / 180;
    var c = Math.cos(a), s = Math.sin(a);

    // current foot
    var d = startingFoot * (1 - 2 * (n % 2));

    // apply equations
    var footprintX = Math.round(
    startingPosX +             // initial
    stepSize * n * c +         // step
    stepWidth * 0.5 * d * s    // foot offset
    );
    var footprintY = Math.round(
        startingPosY +             // initial
        stepSize * n * s -         // step
        stepWidth * 0.5 * d * c    // foot offset
    );
    
    var footPrint = game.add.sprite(0, 0, 'roomLoadingSceneImage602460944');
    footPrint.anchor.x = Math.round(footPrint.width * 0.5) / footPrint.width;
    footPrint.anchor.y = Math.round(footPrint.height * 0.5) / footPrint.height;
    footPrint.x = footprintX;
    footPrint.y = footprintY;
    footPrint.pivot.x = Math.round(footPrint.width * 0.5) / footPrint.width;
    footPrint.pivot.y = Math.round(footPrint.height * 0.5) / footPrint.height;
    footPrint.angle = startingAngle;
    footPrint.alpha = 0;

    /* Footprints start on the left side initially, if it's a right hand one, then flip it START */
    if ((startingFootPrintLeft == false))
    {
        footPrint.scale.y *= -1;
    }
    /* Footprints start on the left side initially, if it's a right hand one, then flip it END */


    var fadeInTransition = game.add.tween(footPrint).to({ alpha: 0.5 }, 1000, Phaser.Easing.none).start();
    var fadeOutTransition = game.add.tween(footPrint).to({ alpha: 0 }, 1000, Phaser.Easing.none);
    fadeInTransition.onComplete.add(function () {

        if ((startingFootPrintLeft == true))
        {
            startingFootPrintLeft = false;
        }
        else
        {
            startingFootPrintLeft = true;
        }


        if ((footPrint.x > systemSettings.resolution.width) || (footPrint.x < 0) || (footPrint.y > systemSettings.resolution.height) || (footPrint.y < 0))
        {
            setNewFootPrintPathFigures();
            startFootPrints(0);
        }
        else
        {
            startFootPrints(n+1);
        }
        
        fadeOutTransition.start();

    }, this);

}

